    //firs label
    private void lbl00MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lbl00MouseClicked
        //each label must call the method where the player will write must 
        Validations game = new Validations();        
        JLabel [] labels = {lbl00, lbl01, lbl02, lbl10, lbl11, lbl12, lbl20, lbl21, lbl22};
        game.checkAllLabels(labels);
        game.setAllNull();        
        game.setTurn(labels);       
        game.checkLabel(lbl00);
        game.checkVictory(labels);
        game.setFinishGame(labels);
        game.setEndGame();        
        if (game.getEndGame()) {
            JOptionPane.showMessageDialog(null, game.getFinishGame());
        } else if (!game.getEndGame() && game.getNullAccount() == 1) {
            JOptionPane.showMessageDialog(null, game.getFinishGame());
        }        
    }//GEN-LAST:event_lbl00MouseClicked
     
